    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       // Botón de subir estación:
         if (jToggleButton1.isSelected())
             myRadio.subirEstacion();
                     
         refreshGui();
                 
      
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Botón de bajar estación:
         if (jToggleButton1.isSelected())
             myRadio.bajarEstacion();
                     
         refreshGui();
    }//GEN-LAST:event_jButton3ActionPerformed

    private void refreshGui()
    {
        if (myRadio.getEncendido()){
            if (myRadio.getFrecuencia())
            {
                jLabel1.setText(String.valueOf(myRadio.getEstacionAM()));
                jLabel2.setText("AM");
            }
            else 
            {
                jLabel1.setText(String.valueOf(myRadio.getEstacionFM()));
                jLabel2.setText("FM");
            }
        }
        else {
            jLabel1.setText("-");
        }
    }
